try {
  // Ottieni i dati JSON del form
  const formData = $input.first().json;

  // Il campo del file dal form
  const fileField = formData["Carica il tuo documento (Carta d'identit√† o Patente)"];

  if (!fileField || !Array.isArray(fileField) || fileField.length === 0) {
    throw new Error('Nessun file caricato nel form');
  }

  // Prendi il primo file
  const fileInfo = fileField[0];

  // IMPORTANTE: n8n converte i nomi dei campi binari sostituendo caratteri speciali
  // Il nome reale del campo binario √® quello che vediamo nel debug
  const binaryPropertyName = "Carica_il_tuo_documento__Carta_d_identit__o_Patente_";

  // Accedi ai dati binari
  const binaryData = $input.first().binary;

  if (!binaryData || !binaryData[binaryPropertyName]) {
    throw new Error(`Dati binari non trovati con chiave: ${binaryPropertyName}`);
  }

  // Ottieni i dati binari specifici
  const fileBinaryData = binaryData[binaryPropertyName];

  // Estrai informazioni
  const fileName = fileInfo.filename;
  const mimeType = fileInfo.mimetype;
  const fileSize = fileInfo.size;

  // Inizializza i controlli di qualit√†
  const qualityChecks = {
    fileSize: { passed: false, message: '' },
    fileType: { passed: false, message: '' },
    imageQuality: { passed: false, message: '' }
  };

  // 1. CONTROLLO DIMENSIONE FILE (max 10MB)
  const maxSize = 10 * 1024 * 1024; // 10MB
  const minSize = 50 * 1024; // 50KB

  if (fileSize > maxSize) {
    qualityChecks.fileSize.passed = false;
    qualityChecks.fileSize.message = `File troppo grande: ${(fileSize/1024/1024).toFixed(2)}MB (max 10MB)`;
  } else if (fileSize < minSize) {
    qualityChecks.fileSize.passed = false;
    qualityChecks.fileSize.message = `File troppo piccolo: ${(fileSize/1024).toFixed(0)}KB (min 50KB)`;
  } else {
    qualityChecks.fileSize.passed = true;
    qualityChecks.fileSize.message = `Dimensione OK: ${(fileSize/1024/1024).toFixed(2)}MB`;
  }

  // 2. CONTROLLO TIPO FILE
  const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png', 'application/pdf'];
  if (allowedTypes.includes(mimeType)) {
    qualityChecks.fileType.passed = true;
    qualityChecks.fileType.message = `Formato supportato: ${mimeType}`;
  } else {
    qualityChecks.fileType.passed = false;
    qualityChecks.fileType.message = `Formato non supportato: ${mimeType}`;
  }

  // 3. ANALISI QUALIT√Ä IMMAGINE
  if (mimeType.startsWith('image/')) {
    const estimatedQuality = fileSize / 1024; // KB

    if (estimatedQuality < 100) {
      qualityChecks.imageQuality.passed = false;
      qualityChecks.imageQuality.message = 'Qualit√† insufficiente - immagine troppo compressa';
    } else if (estimatedQuality < 200) {
      qualityChecks.imageQuality.passed = true;
      qualityChecks.imageQuality.message = 'Qualit√† accettabile ma non ottimale';
    } else {
      qualityChecks.imageQuality.passed = true;
      qualityChecks.imageQuality.message = 'Qualit√† ottima per OCR';
    }
  } else if (mimeType === 'application/pdf') {
    qualityChecks.imageQuality.passed = true;
    qualityChecks.imageQuality.message = 'PDF - qualit√† non verificabile';
  }

  // VALUTAZIONE FINALE
  const allPassed = Object.values(qualityChecks).every(check => check.passed);
  const validationStatus = allPassed ? 'PASSED' : 'REJECTED';

  // Calcola quality score
  let qualityScore = 0;
  if (qualityChecks.fileSize.passed) qualityScore += 33;
  if (qualityChecks.fileType.passed) qualityScore += 33;
  if (qualityChecks.imageQuality.passed) qualityScore += 34;

  // Prepara l'output
  const result = {
    json: {
      validationStatus: validationStatus,
      passed: allPassed,
      fileName: fileName,
      mimeType: mimeType,
      fileSize: fileSize,
      fileSizeReadable: `${(fileSize/1024/1024).toFixed(2)}MB`,
      qualityChecks: qualityChecks,
      qualityScore: qualityScore,
      userMessage: allPassed ? 
        '‚úÖ Documento valido e pronto per elaborazione OCR' : 
        '‚ùå Documento non supera i controlli di qualit√†',
      validatedAt: new Date().toISOString()
    }
  };

  // Se PASSED, passa i dati binari con il nome corretto
  if (allPassed) {
    result.binary = {
      data: fileBinaryData  // Usa l'oggetto binary del file
    };
  }

  return result;

} catch (error) {
  return {
    json: {
      validationStatus: 'REJECTED',
      passed: false,
      error: error.message,
      userMessage: `‚ùå Errore validazione: ${error.message}`,
      validatedAt: new Date().toISOString()
    }
  };
}

