const input = items[0].json;

const dataConsegnaRaw = input["shipment_arrival_date"];
const dataRichiestaRaw = input["data inoltro richiesta"];

// Funzione per generare codice richiesta alfanumerico
function generaCodiceRichiesta(length = 10) {
  const charset = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
  let codice = '';
  for (let i = 0; i < length; i++) {
    const randomIndex = Math.floor(Math.random() * charset.length);
    codice += charset[randomIndex];
  }
  return codice;
}

if (!dataConsegnaRaw || !dataRichiestaRaw) {
  return [{
    json: {
      ...input,
      "errore": "Dati mancanti",
      "reso richiesto entro 14 giorni": null,
      "codice_richiesta": generaCodiceRichiesta()
    }
  }];
}

try {
  // Normalizza entrambe le date in formato YYYY-MM-DD
  const dataConsegna = new Date(dataConsegnaRaw);
  const dataRichiesta = new Date(dataRichiestaRaw);

  const giornoConsegnaStr = dataConsegna.toISOString().slice(0, 10);
  const giornoRichiestaStr = dataRichiesta.toISOString().slice(0, 10);

  const giornoConsegna = new Date(giornoConsegnaStr);
  const giornoRichiesta = new Date(giornoRichiestaStr);

  const diffInMs = giornoRichiesta - giornoConsegna;
  const diffInGiorni = diffInMs / (1000 * 60 * 60 * 24);
  const entro14 = diffInGiorni >= 0 && diffInGiorni <= 14;

  return [{
    json: {
      ...input,
      "shipment_arrival_date_normalizzata": giornoConsegnaStr,
      "data_inoltro_richiesta_normalizzata": giornoRichiestaStr,
      "reso richiesto entro 14 giorni": entro14,
      "giorni passati": diffInGiorni,
      "codice_richiesta": generaCodiceRichiesta()
    }
  }];
} catch (error) {
  return [{
    json: {
      ...input,
      "errore": "Errore nel parsing delle date",
      "dettaglio": error.message,
      "codice_richiesta": generaCodiceRichiesta()
    }
  }];
}

