// üìä REPORT AGGREGATOR 
// Con metriche complete e task urgenti

const tasks = $items("TASK").map(i => i.json);
const emails = $items("MAIL").map(i => i.json);

// ‚è∞ DETERMINA PERIODO REPORT
const now = new Date();
const currentHour = now.getHours();
const isEveningReport = currentHour >= 20;
const reportType = isEveningReport ? "Giornata Completa" : "Prima Met√† Giornata";

// üìà CALCOLA METRICHE AVANZATE
const metrics = {
  totalTasks: tasks.length,
  completedTasks: tasks.filter(t => t.status === 'completed').length,
  pendingTasks: tasks.filter(t => t.status === 'pending').length,
  overdueTasks: tasks.filter(t => 
    t.status === 'pending' && t.deadline && new Date(t.deadline) < now
  ).length,

  // Per priorit√†
  criticalTasks: tasks.filter(t => t.priority === 'critical').length,
  highTasks: tasks.filter(t => t.priority === 'high').length,
  mediumTasks: tasks.filter(t => t.priority === 'medium').length,
  lowTasks: tasks.filter(t => t.priority === 'low').length,

  // Gap analysis
  gapsDetected: tasks.filter(t => 
    t.conversation_context?.analysis?.gap_identified
  ).length,

  // Clienti unici
  uniqueCustomers: [...new Set(tasks.map(t => t.customer_email))].filter(Boolean).length,

  // Email metrics
  totalEmails: emails.length,
  emailsWithAI: emails.filter(e => e.ai_response_sent).length
};

// üö® TASK URGENTI (solo critici e high pendenti)
const urgentPending = tasks
  .filter(t => ['critical', 'high'].includes(t.priority) && t.status === 'pending')
  .sort((a, b) => new Date(a.deadline || '2099-01-01') - new Date(b.deadline || '2099-01-01'))
  .slice(0, 5)
  .map(t => ({
    priority: t.priority,
    title: (t.title || 'Senza titolo').substring(0, 50) + (t.title?.length > 50 ? '...' : ''),
    customer: t.customer_email || 'N/A',
    deadline: t.deadline ? new Date(t.deadline) : null,
    isOverdue: t.deadline ? new Date(t.deadline) < now : false
  }));

// üìä BREAKDOWN PER TIPO AZIONE
const actionBreakdown = {};
tasks.forEach(t => {
  const action = t.action_type || 'unknown';
  if (!actionBreakdown[action]) {
    actionBreakdown[action] = { count: 0, completed: 0 };
  }
  actionBreakdown[action].count++;
  if (t.status === 'completed') {
    actionBreakdown[action].completed++;
  }
});

// Performance rates
const completionRate = Math.round(metrics.completedTasks/metrics.totalTasks*100) || 0;
const gapRate = Math.round(metrics.gapsDetected/metrics.totalTasks*100) || 0;

// üì± FORMATO TELEGRAM
let telegramText = `üìä **REPORT ${reportType.toUpperCase()}**\n`;
telegramText += `üóìÔ∏è ${now.toLocaleDateString('it-IT', { weekday: 'long', day: 'numeric', month: 'long' })}\n`;
telegramText += `‚è∞ Ore: ${now.toLocaleTimeString('it-IT', {hour: '2-digit', minute:'2-digit'})}\n\n`;

telegramText += `**üìà PANORAMICA**\n`;
telegramText += `‚Ä¢ Task Creati: **${metrics.totalTasks}**\n`;
telegramText += `‚Ä¢ Completati: ‚úÖ ${metrics.completedTasks} (${completionRate}%)\n`;
telegramText += `‚Ä¢ In Attesa: ‚è≥ ${metrics.pendingTasks}\n`;
telegramText += `‚Ä¢ In Ritardo: üö® **${metrics.overdueTasks}**\n\n`;

telegramText += `**üéØ PRIORIT√Ä**\n`;
telegramText += `‚Ä¢ üî¥ Critical: ${metrics.criticalTasks}\n`;
telegramText += `‚Ä¢ üü† High: ${metrics.highTasks}\n`;
telegramText += `‚Ä¢ üü° Medium: ${metrics.mediumTasks}\n`;
telegramText += `‚Ä¢ üü¢ Low: ${metrics.lowTasks}\n\n`;

telegramText += `**üìã PER TIPO AZIONE**\n`;
Object.entries(actionBreakdown).forEach(([action, data]) => {
  telegramText += `‚Ä¢ ${action}: ${data.count} (‚úÖ ${data.completed})\n`;
});

telegramText += `\n**üîç ANALISI**\n`;
telegramText += `‚Ä¢ Gap Rilevati: **${metrics.gapsDetected}** (${gapRate}%)\n`;
telegramText += `‚Ä¢ Clienti Gestiti: ${metrics.uniqueCustomers}\n\n`;

telegramText += `**üìß EMAIL**\n`;
telegramText += `‚Ä¢ Processate: ${metrics.totalEmails}\n`;
telegramText += `‚Ä¢ Con AI: ${metrics.emailsWithAI}\n`;

// Aggiungi task urgenti se presenti
if (urgentPending.length > 0) {
  telegramText += `\n**üö® TASK URGENTI APERTI**\n`;
  urgentPending.forEach((t, i) => {
    const icon = t.priority === 'critical' ? 'üî¥' : 'üü†';
    const overdue = t.isOverdue ? ` ‚ö†Ô∏è SCADUTO` : '';
    telegramText += `\n${i+1}. ${icon} ${t.title}\n`;
    telegramText += `   üë§ ${t.customer}\n`;
    if (t.deadline) {
      telegramText += `   ‚è∞ ${t.deadline.toLocaleString('it-IT', {day: '2-digit', month: '2-digit', hour: '2-digit', minute: '2-digit'})}${overdue}\n`;
    }
  });
}

telegramText += `\n---\n`;
telegramText += `üìä **Performance**: ${completionRate}% completion | ${gapRate}% gap detection`;

// Return per backward compatibility
return [{
  json: {
    report: telegramText,
    date: now.toLocaleDateString('it-IT'),
    metrics: metrics
  }
}];

